  left: 0;
  width: 100%;
  height: 100vh;
  transform: scale(0.2);
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  gap: 2rem;
  opacity: 0;
  z-index: -1;
  background: rgba(0, 0, 0, 0);
  overflow-y: scroll;
  overscroll-behavior: contain;
  transition: all 1s;
}
.contact-wrapper::after {
  content: "";
  width: 1rem;
  height: 1rem;
  position: absolute;
  bottom: -0.5vh;
}
.contact-wrapper .contact-form {
  width: 85%;
  height: 80%;
  max-width: 625px;
  padding: 2rem 1rem;
  background: #ffeac8;
  display: flex;
  flex-direction: column;
  gap: 0.5rem;
}
.contact-wrapper .contact-form input,
.contact-wrapper .contact-form textarea {
  font-size: 1rem;
  border: transparent;
  padding: 0.5rem 0.25rem;
  border-radius: 0.25rem;
}
.contact-wrapper .contact-form input:focus,
.contact-wrapper .contact-form textarea:focus {
  outline: 1px solid #dadd19;
}
.contact-wrapper .contact-form button {
  width: 40%;
  padding: 0.35rem 0.85rem;
  margin-left: auto;
  margin-right: 0;
  margin-top: 1rem;
  border-radius: 0.25rem;
  border: transparent;
  font-size: 1rem;
  letter-spacing: 0.1rem;
  background: white;
  transition: all 0.5s;
}
.contact-wrapper .contact-form button:hover {
  background: rgba(160, 160, 160, 0.5);
  color: white;
}
.contact-wrapper .contact-close-btn {
  align-self: flex-end;
  color: white;
  margin-right: 3rem;
  font-size: 2rem;
  transition: color 0.3s;
  cursor: pointer;
}
.contact-wrapper .contact-close-btn:hover {
  color: orangered;
}

.show-contact-section {
  z-index: 2;
  opacity: 1;
  transform: scale(1);
}

.places-section {
  overflow: hidden;
  width: 100vw;
  height: 100vh;
  display: grid;
  place-items: center;
}

#places-card-carousel {
  position: relative;
  display: flex;
  flex-direction: column;
  width: 60vw;
  height: 80vh;
}
#places-card-carousel .slide-btn {
  position: absolute;
  top: 50%;
  transform: translateY(-50%);
  background: transparent;
  border: none;
  font-size: 4rem;
  color: orangered;
  z-index: 6;
  transition: all 0.3s ease-in-out;
}
#places-card-carousel .slide-btn:hover {
  transform: scale(1.2) translateY(-50%);
}
#places-card-carousel .prev-btn {
  right: -3rem;
}
#places-card-carousel .prev-btn:disabled {
  color: rgba(134, 42, 6, 0.7);
}
#places-card-carousel .next-btn {
  left: -3rem;
}
#places-card-carousel .next-btn:disabled {
  color: rgba(134, 42, 6, 0.7);
}

.places-wrapper {
  position: relative;
  width: 100%;
  height: 100%;
}

.place-card {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  padding: 0 1rem;
  background: khaki;
  display: flex;
  flex-direction: column;
  justify-content: space-evenly;
  align-items: center;
  gap: 0.5rem;
  cursor: pointer;
  will-change: contents;
  box-shadow: 2px 2px 5px rgba(160, 160, 160, 0.5);
  transition: all 0.3s ease-in-out;
}
.place-card:hover {
  transform: scale(1.025);
  border: 1px solid rgba(134, 42, 6, 0.7);
  z-index: 3;
}
.place-card .country-name {
  position: absolute;
  top: 0;
  padding: 0.1rem 1.5rem;
  background-color: rgba(134, 42, 6, 0.7);
  letter-spacing: 2px;
  color: white;
}
.place-card .country-map {
  margin-top: 1.5rem;
  max-height: 42.5%;
}
.place-card .country-title {
  width: 75%;
  text-align: center;
  font-size: 1.35rem;
  font-weight: bold;
  color: white;
  letter-spacing: 0.2rem;
  line-height: 1.2;
  text-shadow: 0 1px 2px #373737;
}
.place-card .gallery-card,
.place-card .journal-card {
  font-size: 1.375rem;
  transition: all 0.5s;
}
.place-card .gallery-card:hover,
.place-card .journal-card:hover {
  color: rgba(134, 42, 6, 0.7);
  transform: scale(1.1);
}
.place-card p {
  display: flex;
  gap: 0.5rem;
}
.place-card .underline {
  position: absolute;
  bottom: 15%;
  width: 50%;
  height: 0.2rem;
  background: rgba(134, 42, 6, 0.7);
  border-radius: 5rem;
  transition: width 0.5s ease-in-out;
}
.place-card .underline:hover {
  width: 10%;
}

