.main-wrapper {
  position: relative;
  width: 100vw;
  min-height: 100vh;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  padding-bottom: 6rem;
}
.main-wrapper .main-section-title {
  font-size: clamp(3.5rem, 15vw, 5rem);
  margin-top: 3rem;
  color: #373737;
  text-shadow: 1px 3px 5px #373737;
  letter-spacing: 0.3rem;
}
.main-wrapper .gallery {
  padding: 3rem 0;
  width: 90vw;
  display: grid;
  grid-template-columns: 1fr;
  grid-auto-rows: auto;
  gap: 1rem;
}
.main-wrapper .gallery .gallery-image {
  border: 5px solid white;
  box-shadow: 1px 1px 5px black;
  display: flex;
  flex-direction: column;
}
.main-wrapper .gallery .gallery-image .image-info-wrapper {
  margin-top: 0.5rem;
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  align-items: center;
}
.main-wrapper .gallery .gallery-image .image-info-wrapper .photo-number {
  padding: 0.25rem 0.5rem;
  font-size: 1.25rem;
  text-align: right;
}
.main-wrapper .gallery .gallery-image .image-info-wrapper .photo-place {
  padding: 0.5rem 0.25rem;
  font-size: 1.25rem;
}
@media screen and (min-width: 660px) {
  .main-wrapper .gallery {
    grid-template-columns: repeat(2, 1fr);
  }
  .main-wrapper .gallery .gallery-image {
    transition: all, 0.5s ease-in-out;
    cursor: pointer;
  }
  .main-wrapper .gallery .gallery-image:hover {
    border: 5px solid #dadd19;
    transform: scale(1.05);
    z-index: 10;
  }
  .main-wrapper .gallery .gallery-image .image-info-wrapper {
    display: none;
  }
  .main-wrapper .gallery::after {
    content: "full-slide";
    font-size: 0;
  }
}
@media screen and (min-width: 800px) {
  .main-wrapper .gallery {
    grid-template-columns: repeat(3, 1fr);
  }
}
@media screen and (min-width: 1000px) {
  .main-wrapper .gallery {
    grid-template-columns: repeat(4, 1fr);
  }
}

#gallery-full-carousel-wrapper {
  display: flex;
  flex-direction: column;
  opacity: 0;
  background-color: #862a06;
  position: absolute;
  top: 0;
  left: 0;
  width: 100vw;
  height: 100vh;
  z-index: -9;
  transition: all 0.5s ease-in-out;
}

#close-gallery-btn {
  position: absolute;
  align-self: flex-end;
  right: 1rem;
  bottom: calc(7rem - 5vw);
  color: white;
  font-size: 5rem;
  background: transparent;
  border: none;
  opacity: 0.4;
  z-index: 99;
  transition: all 0.3s ease-in-out;
  will-change: contents;
}
#close-gallery-btn:hover {
  opacity: 0.7;
  color: orangered;
  transform: scale(1.1);
}

@media screen and (min-width: 800px) {
  #close-gallery-btn {
    bottom: calc(7rem - 10vw);
  }
}
@media screen and (min-width: 1000px) {
  #close-gallery-btn {
    right: calc(2.5rem + 2vw);
    bottom: 0.5rem;
  }
}
@media screen and (min-width: 1150px) {
  #close-gallery-btn {
    right: calc(5rem + 10vw);
  }
}
@media screen and (min-width: 660px) and (min-height: 900px) {
  #close-gallery-btn {
    color: orangered;
  }
  #close-gallery-btn:hover {
    color: #862a06;
  }
}
@media screen and (min-width: 1100px) and (min-height: 900px) {
  #close-gallery-btn {
    opacity: 0.4;
    right: clamp(4rem, 5vw, 5rem);
