    font-size: 6rem;
  }
}
.gallery-full-carousel {
  transition: all 0.3s ease-in-out;
  position: relative;
  background: rgba(192, 211, 216, 0.952);
  width: 100%;
  height: 100%;
  transition: all 0.5s linear;
}
.gallery-full-carousel .full-carousel-image {
  position: absolute;
  width: clamp(500px, 100%, 1000px);
  height: auto;
  max-height: 100vh;
  opacity: 0;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%) !important;
  display: flex;
  justify-content: center;
  align-items: center;
  transition: all 1.5s;
}
.gallery-full-carousel .full-carousel-image .img-slide {
  margin: 0 auto;
}
@media screen and (min-height: 900px) {
  .gallery-full-carousel .full-carousel-image {
    width: clamp(500px, 100%, 1300px);
  }
}
.gallery-full-carousel .full-carousel-image .photo-number {
  position: absolute;
  top: 2vh;
  right: 5vw;
  font-size: 1.35rem;
  opacity: 0.8;
  color: white;
  text-shadow: 1px 1px black;
  letter-spacing: 0.2rem;
}
.gallery-full-carousel .full-carousel-image .photo-place {
  position: absolute;
  top: 2vh;
  left: 5vw;
  font-size: 1.35rem;
  opacity: 0.8;
  color: white;
  text-shadow: 1px 1px black;
  letter-spacing: 0.2rem;
}
.gallery-full-carousel .next {
  transform: translate(150%, -50%) !important;
  border-left: 3px solid white;
  border-right: 3px solid white;
}
.gallery-full-carousel .last {
  transform: translate(-150%, -50%) !important;
  border-left: 3px solid #0a0a0a;
  border-right: 3px solid white;
}
.gallery-full-carousel .active {
  opacity: 1;
