  z-index: 99 !important;
}

#journal {
  width: 100vw;
  padding-bottom: 3rem;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
}
#journal .journal-text-wrapper:nth-child(odd) .journal-image {
  float: left !important;
  margin-right: 1rem;
  margin-bottom: 0.5rem;
}
#journal .journal-text-wrapper:nth-child(even) .journal-image {
  float: right !important;
  margin-left: 1rem;
  margin-bottom: 0.5rem;
}
#journal #journal-pages {
  margin-top: 2rem;
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: center;
  gap: 1.5rem;
}
#journal #journal-pages [data-page] {
  display: grid;
  place-items: center;
  width: 2rem;
  height: 2rem;
  border-radius: 50%;
  color: #373737;
  font-size: 2rem;
  line-height: 1.1;
  margin: 0 auto;
  cursor: pointer;
  transition: all 0.3s ease-in-out;
}
#journal #journal-pages [data-page]:hover {
  background-color: rgba(192, 211, 216, 0.952);
  color: orangered;
}

.journal-header {
  width: 100vw;
  height: 35vh;
  box-shadow: 0 0 15px #3b2c18;
  background: radial-gradient(circle, rgba(116, 99, 46, 0.2) 0%, rgba(238, 196, 56, 0.3) 52%, rgba(255, 255, 255, 0.6) 100%), url(/e12ba3aa6932f5358e665b8bb216ebc6.JPG);
  background-position: center;
  background-size: cover;
  background-repeat: no-repeat;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
}
.journal-header .journal-banner-title {
  color: #3b2c18;
  text-shadow: 1px 1px 15px rgba(192, 211, 216, 0.952);
  font-size: clamp(2.5rem, 12vw, 4rem);
  letter-spacing: 2px;
  text-align: center;
}
.journal-header .journal-banner-text {
  font-size: 1.5rem;
  font-size: clamp(1rem, 5vw, 1.5rem);
  width: 90%;
  color: white;
  text-align: center;
  letter-spacing: 2px;
  text-shadow: 3px 3px 10px black;
}

.journal-text-wrapper {
  width: 100%;
  max-width: 900px;
  margin: 0 auto;
  margin-top: 3rem;
  padding-top: 2.5rem;
  padding-bottom: 2.5rem;
  padding-left: clamp(2rem, 7vw, 5rem);
  padding-right: clamp(2rem, 7vw, 5rem);
  box-shadow: 5px 5px 20px rgba(160, 160, 160, 0.5);
}
.journal-text-wrapper .journal-title {
  font-size: 1.75rem;
  word-wrap: break-word;
}
.journal-text-wrapper .journal-info {
  padding: 1rem 0;
  display: flex;
  flex-direction: column;
  gap: 0.5rem;
}
.journal-text-wrapper .journal-info p {
  font-size: 1.25rem;
}
.journal-text-wrapper .journal-text {
  display: block;
  padding: 1rem 0;
  text-align: justify;
}
.journal-text-wrapper .journal-text span {
  font-size: 1rem;
  text-indent: 1rem;
  line-height: 1.25;
  letter-spacing: 0.1rem;
  display: block;
  padding: 0.5rem 0;
}
.journal-text-wrapper .journal-text .journal-image {
  width: 100vw;
  display: inline-flex;
}

@media screen and (min-width: 660px) {
  .journal-text-wrapper {
    width: 90%;
  }
  .journal-text-wrapper .journal-image {
    width: 90%;
  }
}
@media screen and (min-width: 800px) {
  .journal-text-wrapper {
    width: 85%;
  }
