// ********* CONTACT SECTION ********* //
.contact-wrapper {
	position: absolute;
	top: 0;
	left: 0;
	width: 100%;
	height: 100vh;
	transform: scale(0.2);
	display: flex;
	flex-direction: column;
	justify-content: center;
	align-items: center;
	gap: 2rem;
	opacity: 0;
	z-index: -1;
	background: rgba(0, 0, 0, 0);
	overflow-y: scroll;
	overscroll-behavior: contain;
	transition: all 1s;
	&::after {
		//** pseudo element add to force scroll Y into section
		//** necessary to JS code for stop scrolling beyond current section (same as ABOUT and ROUTE sections)
		content: "";
		width: 1rem;
		height: 1rem;
		position: absolute;
		bottom: -0.5vh;
	}
	.contact-form {
		width: 85%;
		// width: 80%;
		height: 80%;
		max-width: 625px;
		padding: 2rem 1rem;
		// padding: 2rem 3rem;
		background: rgb(255, 234, 200);
		display: flex;
		flex-direction: column;
		gap: 0.5rem;
		input,
		textarea {
			font-size: 1rem;
			// font-size: 1.15rem;
			border: transparent;
			padding: 0.5rem 0.25rem;
			border-radius: 0.25rem;
			&:focus {
				outline: 1px solid $color-primary-100;
			}
		}
		button {
			width: 40%;
			// width: 20%;
			padding: 0.35rem 0.85rem;
			margin-left: auto;
			margin-right: 0;
			margin-top: 1rem;
			border-radius: 0.25rem;
			border: transparent;
			font-size: 1rem;
			// font-size: 1.15rem;
			letter-spacing: 0.1rem;
			background: $color-light-100;
			transition: all 0.5s;
			&:hover {
				background: $color-dark-100;
				color: $color-light-100;
			}
		}
	}
	.contact-close-btn {
		align-self: flex-end;
		color: $color-light-100;
		margin-right: 3rem;
		font-size: 2rem;
		transition: color 0.3s;
		cursor: pointer;
		&:hover {
			color: $color-primary-300;
		}
	}
}
.show-contact-section {
	z-index: 2;
	opacity: 1;
	transform: scale(1);
}

