// ********* CAROUSEL COUNTRIES MENU CARDS ********* //
.places-section {
	overflow: hidden;
	width: 100vw;
	height: 100vh;
	display: grid;
	place-items: center;
}
#places-card-carousel {
	position: relative;
	display: flex;
	flex-direction: column;
	width: 60vw;
	height: 80vh;
	.slide-btn {
		position: absolute;
		top: 50%;
		transform: translateY(-50%);
		background: transparent;
		border: none;
		font-size: 4rem;
		color: $color-primary-300;
		z-index: 6;
		transition: $transition-500;
		&:hover {
			transform: scale(1.2) translateY(-50%);
		}
	}
	.prev-btn {
		right: -3rem;
		&:disabled {
			color: $color-primary-500;
		}
	}
	.next-btn {
		left: -3rem;
		&:disabled {
			color: $color-primary-500;
		}
	}
}
.places-wrapper {
	position: relative;
	width: 100%;
	height: 100%;
}
.place-card {
	position: absolute;
	top: 0;
	left: 0;
	width: 100%;
	height: 100%;
	padding: 0 1rem;
	background: khaki;
	display: flex;
	flex-direction: column;
	justify-content: space-evenly;
	align-items: center;
	gap: 0.5rem;
	cursor: pointer;
	will-change: contents;
	box-shadow: 2px 2px 5px $color-dark-100;
	transition: all 0.3s ease-in-out;

	&:hover {
		transform: scale(1.025);
		border: 1px solid $color-primary-500;
		z-index: 3;
	}
	.country-name {
		position: absolute;
		top: 0;
		padding: 0.1rem 1.5rem;
		background-color: $color-primary-500;
		letter-spacing: $letter-spacing-200;
		color: $color-light-100;
	}
	.country-map {
		margin-top: 1.5rem;
		max-height: 42.5%;
	}
	.country-title {
		width: 75%;
		// margin-top: 5rem;
		text-align: center;
		font-size: 1.35rem;
		font-weight: bold;
		color: $color-light-100;
		letter-spacing: 0.2rem;
		line-height: 1.2;
		text-shadow: 0 1px 2px $color-dark-500;
	}
	.gallery-card,
	.journal-card {
		font-size: 1.375rem;
		transition: all 0.5s;
		&:hover {
			color: $color-primary-500;
			transform: scale(1.1);
		}
	}

	p {
		display: flex;
		gap: 0.5rem;
	}
	.underline {
		position: absolute;
		bottom: 15%;
		width: 50%;
		height: 0.2rem;
		background: $color-primary-500;
		border-radius: 5rem;
		transition: width 0.5s ease-in-out;
		&:hover {
			width: 10%;
		}
	}
}
@media screen and (min-width: 770px) {
	#places-card-carousel {
