// ********* GALLERY  IMAGES ********* //
.main-wrapper {
	position: relative;
	width: 100vw;
	min-height: 100vh;
	display: flex;
	flex-direction: column;
	justify-content: center;
	align-items: center;
	padding-bottom: 6rem;
	//=== gallery general view
	.main-section-title {
		font-size: clamp(3.5rem, 15vw, 5rem);
		margin-top: 3rem;
		color: $color-dark-500;
		text-shadow: 1px 3px 5px $color-dark-500;
		letter-spacing: 0.3rem;
	}
	.gallery {
		padding: 3rem 0;
		// position: relative;
		width: 90vw;
		display: grid;
		grid-template-columns: 1fr;
		grid-auto-rows: auto;
		gap: 1rem;
		.gallery-image {
			border: 5px solid $color-light-100;
			box-shadow: 1px 1px 5px $color-dark-900;
			display: flex;
			flex-direction: column;
			.image-info-wrapper {
				margin-top: 0.5rem;
				display: flex;
				flex-direction: row;
				justify-content: space-between;
				align-items: center;

				.photo-number {
					padding: 0.25rem 0.5rem;
					font-size: 1.25rem;
					text-align: right;
				}
				.photo-place {
					padding: 0.5rem 0.25rem;
					font-size: 1.25rem;
				}
			}
		}
	}
	@media screen and (min-width: 660px) {
		.gallery {
			grid-template-columns: repeat(2, 1fr);
			.gallery-image {
				transition: all, 0.5s ease-in-out;
				cursor: pointer;
				&:hover {
					border: 5px solid $color-primary-100;
					transform: scale(1.05);
					z-index: 10;
				}
				.image-info-wrapper {
					display: none;
				}
			}
			//for JS event listener : smaller than this width the full screen slide is disabled
			&::after {
				content: "full-slide";
				font-size: 0;
			}
		}
	}
	@media screen and (min-width: 800px) {
		.gallery {
			grid-template-columns: repeat(3, 1fr);
		}
	}
	@media screen and (min-width: 1000px) {
		.gallery {
			grid-template-columns: repeat(4, 1fr);
		}
	}
}
// 	GALLERY FULL CAROUSEL
#gallery-full-carousel-wrapper {
	display: flex;
	flex-direction: column;
	opacity: 0;
	background-color: $color-primary-600;
	position: absolute;
