.gallery-full-carousel {
	transition: $transition-500;
	position: relative;
	background: $color-light-200;
	width: 100%;
	height: 100%;
	transition: all 0.5s linear;
	.full-carousel-image {
		position: absolute;
		width: clamp(500px, 100%, 1000px);
		height: auto;
		max-height: 100vh;
		opacity: 0;
		top: 50%;
		left: 50%;
		transform: translate(-50%, -50%) !important;
		display: flex;
		justify-content: center;
		align-items: center;
		transition: all 1.5s;
		.img-slide {
			margin: 0 auto;
		}
		@media screen and(min-height:900px) {
			width: clamp(500px, 100%, 1300px);
		}
		.photo-number {
			position: absolute;
			top: 2vh;
			right: 5vw;
			font-size: 1.35rem;
			opacity: 0.8;
			color: $color-light-100;
			text-shadow: 1px 1px $color-dark-900;
			letter-spacing: 0.2rem;
		}
		.photo-place {
			position: absolute;
			top: 2vh;
			left: 5vw;
			font-size: 1.35rem;
			opacity: 0.8;
			color: $color-light-100;
			text-shadow: 1px 1px $color-dark-900;
			letter-spacing: 0.2rem;
		}
	}
	.next {
		transform: translate(150%, -50%) !important;
		border-left: 3px solid white;
		border-right: 3px solid white;
	}
	.last {
		transform: translate(-150%, -50%) !important;
		border-left: 3px solid rgb(10, 10, 10);
		border-right: 3px solid white;
	}
	.active {
		opacity: 1;
		transform: translate(-50%, -50%) !important;
	}
}
@media screen and(min-width:660px) {
	#gallery-full-carousel-wrapper {
		display: flex;
	}
}

.hide-carousel {
	transform: translateY(-130%) !important;
}
.show-carousel {
	opacity: 1 !important;
	z-index: 99 !important;
}

// ********* end of GALLERY  IMAGES ********* //

// ********* JOURNAL ********* //
#journal {
	width: 100vw;
	padding-bottom: 3rem;
	display: flex;
	flex-direction: column;
	justify-content: center;
	align-items: center;
	.journal-text-wrapper:nth-child(odd) {
		.journal-image {
			float: left !important;
			margin-right: 1rem;
			margin-bottom: 0.5rem;
		}
	}
	.journal-text-wrapper:nth-child(even) {
		.journal-image {
			float: right !important;
			margin-left: 1rem;
			margin-bottom: 0.5rem;
		}
	}
	#journal-pages {
		margin-top: 2rem;
		display: flex;
		flex-direction: row;
		justify-content: center;
		align-items: center;
		gap: 1.5rem;

		[data-page] {
			display: grid;
			place-items: center;
			width: 2rem;
			height: 2rem;
			border-radius: 50%;
			color: $color-dark-500;
			font-size: 2rem;
			line-height: 1.1;
			margin: 0 auto;
			cursor: pointer;
			transition: $transition-500;
			&:hover {
				background-color: $color-light-200;
				color: $color-primary-300;
			}
		}
	}
}
.journal-header {
	width: 100vw;
	height: 35vh;
	box-shadow: 0 0 15px $color-primary-700;
	background: radial-gradient(
			circle,
			rgba(116, 99, 46, 0.2) 0%,
			rgba(238, 196, 56, 0.3) 52%,
			rgba(255, 255, 255, 0.6) 100%
		),
		url(/images/DSC_0210.JPG);
	background-position: center;
	background-size: cover;
	background-repeat: no-repeat;
	display: flex;
	flex-direction: column;
	justify-content: center;
	align-items: center;
	.journal-banner-title {
		color: $color-primary-700;
		text-shadow: 1px 1px 15px $color-light-200;
		font-size: clamp(2.5rem, 12vw, 4rem);
		letter-spacing: $letter-spacing-200;
		text-align: center;
	}
	.journal-banner-text {
		font-size: 1.5rem;
		font-size: clamp(1rem, 5vw, 1.5rem);
		width: 90%;
		color: $color-light-100;
		text-align: center;
		letter-spacing: $letter-spacing-200;
		text-shadow: 3px 3px 10px $color-dark-900;
	}
}
.journal-text-wrapper {
	width: 100%;
	// width: 90%;
	max-width: 900px;
	margin: 0 auto;
	margin-top: 3rem;
	padding-top: 2.5rem;
	padding-bottom: 2.5rem;
	padding-left: clamp(2rem, 7vw, 5rem);
	padding-right: clamp(2rem, 7vw, 5rem);
	box-shadow: 5px 5px 20px $color-dark-100;
	// float: right;

	.journal-title {
		font-size: 1.75rem;
		word-wrap: break-word;
	}
	.journal-info {
		padding: 1rem 0;
		display: flex;
		flex-direction: column;
		gap: 0.5rem;
		// justify-content: space-between;
		& p {
			font-size: 1.25rem;
		}
	}
	.journal-text {
		display: block;
		padding: 1rem 0;
		text-align: justify;
		& span {
			font-size: 1rem;
			text-indent: 1rem;
			// text-indent: 2rem;
			// font-size: 1.15rem;
			line-height: 1.25;
			// line-height: 1.5;
			letter-spacing: 0.1rem;
			display: block;
			padding: 0.5rem 0;
		}
		.journal-image {
			width: 100vw;
			display: inline-flex;
		}
	}
}
@media screen and(min-width: 660px) {
	.journal-text-wrapper {
		width: 90%;
		.journal-image {
			// width: 35vw !important;
			width: 90%;
		}
	}
}
@media screen and(min-width: 800px) {
	.journal-text-wrapper {
		width: 85%;
		.journal-image {
			width: 30vw !important;
		}
	}
}
@media screen and(min-width: 1000px) {
	.journal-text-wrapper {
		width: 80%;
		.journal-image {
			width: 20vw !important;
		}
	}
}
// ********* end of JOURNAL ********* //

// ********* FOOTER ********* //
.footer {
	width: 100vw;
	height: 20vh;
	display: grid;
	place-items: center;
	background-color: $color-primary-600;
	z-index: 100;
	& p {
		display: flex;
		flex-direction: column;
		align-items: center;
		justify-content: center;
		color: $color-light-100;
		font-size: 1.15rem;
		letter-spacing: $letter-spacing-200;
		gap: 0.5rem;
	}
}
@media screen and(min-width:660px) {
	.footer {
		& p {
			flex-direction: row;
			gap: 1.5rem;
		}
	}
}
// ********* end of FOOTER ********* //

// ********* BACK TO TOP BTN ********* //
#back-to-top-btn {
	position: fixed;
	opacity: 0;
	bottom: 5vh;
	right: 2.5vw;
	background: none;
	border: none;
	will-change: contents;
	transition: $transition-500;
	z-index: -1;
	cursor: pointer;
	&:hover {
		animation: bounce 0.5s ease-in-out infinite;
	}
	& i {
		font-size: 3.5rem;
		color: $color-primary-500;
		transition: $transition-500;
		&:hover {
			// transform: scale(1.2);
			color: $color-primary-300;
		}
	}
}
.show-back-btn {
	z-index: 50 !important;
	opacity: 1 !important;
}
@keyframes bounce {
	0% {
		transform: translateY(0);
	}
	50% {
		transform: translateY(-50%);
	}
	100% {
		transform: translateY(0);
	}
}

